<?php

namespace PHPTLS\Tests\Client;

use PHPTLS\Tls\Client\ParseServerHello;
use PHPUnit\Framework\TestCase;

class ParseServerHelloTest extends TestCase
{
    private function getTestDataString()
    {
        $data =
            //ServerHello
            '160303004a0200004603034016c02bfc25c39437c523f7b9cda73d7ecc0fd1309b8c9aafd884f4a45591552075654b2e5fff2ad6517ab57acc7a977a985e3e802723350a3798daf9d11de108009c00' .
            //Certificate
            '' .
            //ServerHelloDone
            '16030300040e000000';
        return hex2bin($data);
    }

    public function testParse()
    {
        $hexString   = $this->getTestDataString();
        $ServerHello = new ParseServerHello($hexString);

        $results = $ServerHello->parse();

        $expectedValue = "160303004a0200004603034016c02bfc25c39437c523f7b9cda73d7ecc0fd1309b8c9aafd884f4a45591552075654b2e5fff2ad6517ab57acc7a977a985e3e802723350a3798daf9d11de108009c00";
        $this->assertEquals($expectedValue, bin2hex($results['ServerHello']));

        $expectedValue = "";
        $this->assertEquals($expectedValue, bin2hex($results['Certificate']));

        $expectedValue = "16030300040e000000";
        $this->assertEquals($expectedValue, bin2hex($results['ServerHelloDone']));
    }
}
